// src/components/Dashboard.js
import React, { useEffect, useState } from 'react';
import VideoStream from './VideoStream';
import EventLog from './EventLog';
import TestModePanel from './TestModePanel';
import { initSocket, disconnectSocket, subscribeToEvent, sendEvent } from '../services/socket';

const Dashboard = () => {
  const [serverMessage, setServerMessage] = useState('');
  const [mode, setMode] = useState('live');

  useEffect(() => {
    initSocket();
    subscribeToEvent('response', (data) => {
        setServerMessage(data.message);
    });

    return () => {
      disconnectSocket();
    };
  }, []);

  const handleModeChange = (newMode) => {
    // 모드가 변경될 때 기존 스트리밍이 있다면 중지 요청
    sendEvent('stop_stream');
    setMode(newMode);
  }

  return (
    <div>
      {/* --- 모드 선택 버튼 --- */}
      <div className="flex justify-center space-x-4 mb-4">
        <button 
          onClick={() => handleModeChange('live')} 
          className={`px-6 py-2 font-bold text-white rounded-lg transition-colors ${mode === 'live' ? 'bg-cyan-600' : 'bg-gray-600 hover:bg-gray-700'}`}>
          실시간 다중 감시
        </button>
        <button 
          onClick={() => handleModeChange('test')} 
          className={`px-6 py-2 font-bold text-white rounded-lg transition-colors ${mode === 'test' ? 'bg-cyan-600' : 'bg-gray-600 hover:bg-gray-700'}`}>
          시험 영상 분석
        </button>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2 space-y-6">
          {/* --- 모드에 따라 다른 컴포넌트 렌더링 --- */}
          {mode === 'live' ? (
            <VideoStream title="실시간 감시 영상 (카메라 1)" streamEvent={{ type: 'start_stream', payload: { camera_id: 0 } }} />
          ) : (
            <TestModePanel />
          )}
        </div>
        <div className="space-y-6">
          {serverMessage && <div className="p-4 bg-blue-900 rounded-lg text-center">{serverMessage}</div>}
          <EventLog />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
