// src/components/ModelSelector.js
import React, { useState, useEffect } from 'react';
import { getModels } from '../services/api';
import { sendEvent, subscribeToEvent, unsubscribeFromEvent } from '../services/socket';

const ModelSelector = () => {
  const [models, setModels] = useState([]);
  const [selectedModel, setSelectedModel] = useState('');
  const [activeModel, setActiveModel] = useState('');

  useEffect(() => {
    const fetchModels = async () => {
      try {
        const { data } = await getModels();
        setModels(data);
        if (data.length > 0) {
          setSelectedModel(data[0].name);
          setActiveModel(data[0].name);
        }
      } catch (error) {
        console.error("모델 목록을 가져오는 데 실패했습니다:", error);
      }
    };
    fetchModels();

    subscribeToEvent('model_changed', (data) => {
      setActiveModel(data.model_name);
    });

    return () => {
      unsubscribeFromEvent('model_changed');
    }
  }, []);

  const handleChange = (e) => {
    setSelectedModel(e.target.value);
  };

  const handleSubmit = () => {
    if (selectedModel) {
      sendEvent('change_model', { model_name: selectedModel });
    }
  };

  return (
    <div className="p-4 bg-gray-800 rounded-lg shadow-lg">
      <h3 className="text-lg font-semibold mb-2 text-white">AI 모델 선택</h3>
      <div className="mb-4">
        <p className="text-sm text-gray-400">현재 활성 모델: <span className="font-bold text-cyan-400">{activeModel}</span></p>
      </div>
      <div className="flex space-x-2">
        <select
          value={selectedModel}
          onChange={handleChange}
          className="w-full px-3 py-2 text-white bg-gray-700 border border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-cyan-500"
        >
          {models.map((model) => (
            <option key={model.name} value={model.name}>
              {model.name}
            </option>
          ))}
        </select>
        <button onClick={handleSubmit} className="px-4 py-2 font-semibold text-white bg-blue-600 rounded-lg hover:bg-blue-700">
          적용
        </button>
      </div>
    </div>
  );
};

export default ModelSelector;
