import React, { useState, useEffect } from 'react';
import { subscribeToEvent, unsubscribeFromEvent, sendEvent } from '../services/socket';

const VideoStream = () => {
  const [frame, setFrame] = useState('');
  const [isStreaming, setIsStreaming] = useState(false);

  useEffect(() => {
    // 'video_frame' 이벤트를 구독하여 서버로부터 오는 프레임을 수신
    subscribeToEvent('video_frame', (data) => {
      // Base64 인코딩된 이미지 데이터를 src에 설정
      setFrame(`data:image/jpeg;base64,${data.image}`);
    });

    // 컴포넌트 언마운트 시 이벤트 구독 해제
    return () => {
      unsubscribeFromEvent('video_frame');
      // 컴포넌트가 사라질 때 스트리밍 중지 요청
      if (isStreaming) {
        sendEvent('stop_stream');
      }
    };
  }, [isStreaming]); // isStreaming 상태가 변경될 때도 useEffect가 다시 실행되도록 의존성 배열에 추가

  const handleStart = () => {
    sendEvent('start_stream', { camera_id: 0 }); // 웹캠(0번) 스트리밍 시작 요청
    setIsStreaming(true);
  };

  const handleStop = () => {
    sendEvent('stop_stream');
    setIsStreaming(false);
    setFrame(''); // 스트리밍 중지 시 이미지 초기화
  };

  return (
    <div className="p-4 bg-gray-800 rounded-lg shadow-lg">
      <h2 className="text-xl font-semibold mb-4 text-white">실시간 감시 영상 (RGB + 모의 TIR)</h2>
      <div className="bg-black border-2 border-cyan-500 rounded-md min-h-[480px] flex items-center justify-center">
        {frame ? (
          <img src={frame} alt="Video Stream" className="w-full h-auto" />
        ) : (
          <p className="text-gray-500">스트리밍이 중지되었습니다.</p>
        )}
      </div>
      <div className="mt-4 flex justify-center space-x-4">
        <button 
          onClick={handleStart} 
          disabled={isStreaming}
          className="px-6 py-2 font-bold text-white bg-green-600 rounded-lg hover:bg-green-700 disabled:bg-gray-500 disabled:cursor-not-allowed">
          스트리밍 시작
        </button>
        <button 
          onClick={handleStop}
          disabled={!isStreaming}
          className="px-6 py-2 font-bold text-white bg-red-600 rounded-lg hover:bg-red-700 disabled:bg-gray-500 disabled:cursor-not-allowed">
          스트리밍 중지
        </button>
      </div>
    </div>
  );
};

export default VideoStream;
