import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import { apiClient } from '../services/api'; // Axios 인스턴스를 직접 사용
import AuthContext from '../context/AuthContext';

const AdminPage = () => {
    const [users, setUsers] = useState([]);
    const [error, setError] = useState('');
    const { user } = useContext(AuthContext);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                // 관리자 전용 API 호출
                const response = await apiClient.get('/api/users');
                setUsers(response.data);
            } catch (err) {
                setError('사용자 목록을 불러오는 데 실패했습니다. 권한을 확인하세요.');
                console.error(err);
            }
        };
        fetchUsers();
    }, []);

    return (
        <div className="min-h-screen flex flex-col">
            <header className="bg-gray-800 shadow-md p-4 flex justify-between items-center">
                <h1 className="text-2xl font-bold text-yellow-400">관리자 페이지</h1>
                <div>
                    <span className="text-gray-300 mr-4">
                        {user?.username}님 (관리자)
                    </span>
                    <Link to="/" className="px-4 py-2 font-bold text-white bg-blue-600 rounded-lg hover:bg-blue-700 mr-2">
                        대시보드로
                    </Link>
                </div>
            </header>
            <main className="flex-grow p-6">
                <div className="p-4 bg-gray-800 rounded-lg shadow-lg">
                    <h2 className="text-xl font-semibold mb-4 text-white">사용자 관리</h2>
                    {error && <p className="text-red-500">{error}</p>}
                    <div className="overflow-x-auto">
                        <table className="min-w-full text-left">
                            <thead className="border-b border-gray-600">
                                <tr>
                                    <th className="p-2">ID</th>
                                    <th className="p-2">사용자명</th>
                                    <th className="p-2">역할</th>
                                </tr>
                            </thead>
                            <tbody>
                                {users.map(u => (
                                    <tr key={u.id} className="border-b border-gray-700 hover:bg-gray-700">
                                        <td className="p-2">{u.id}</td>
                                        <td className="p-2">{u.username}</td>
                                        <td className="p-2">{u.role}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </main>
        </div>
    );
};

export default AdminPage;
