// /frontend/src/components/VideoStream.js

import React from 'react';

const VideoStream = ({ title, frameData, isStreaming, onStreamClick, personDetected }) => {
  // personDetected 값에 따라 부모 div의 배경 클래스를 동적으로 결정
  const containerClass = personDetected
    ? 'bg-blink-warning' // 탐지 시: 강화된 깜빡임 애니메이션 배경
    : 'bg-gray-800'; // 평상시: 일반 배경

  const borderClass = personDetected ? 'border-red-500' : 'border-cyan-500';

  // 디버깅 정보
  const debugInfo = {
    hasFrameData: !!frameData,
    frameDataLength: frameData ? frameData.length : 0,
    isStreaming,
    personDetected
  };

  return (
    // 수정: 동적 배경 클래스 적용
    <div className={`p-4 rounded-lg shadow-lg cursor-pointer transition-all duration-300 ${containerClass}`} onClick={onStreamClick}>
      <h2 className="text-xl font-semibold mb-2 text-white">{title}</h2>
      
      {/* 디버깅 정보 표시 */}
      <div className="mb-2 text-xs text-gray-400 bg-gray-700 p-2 rounded">
        <p>스트리밍: {isStreaming ? '활성' : '비활성'}</p>
        <p>프레임 데이터: {debugInfo.hasFrameData ? '있음' : '없음'} ({debugInfo.frameDataLength} chars)</p>
        <p>탐지 상태: {personDetected ? '탐지됨' : '탐지 안됨'}</p>
      </div>
      
      <div
        className={`bg-black rounded-md aspect-video flex items-center justify-center transition-all duration-300 border-2 ${borderClass} ${personDetected ? 'ring-4 ring-red-500 ring-opacity-50' : ''}`}
      >
        {frameData ? (
          <img 
            src={`data:image/jpeg;base64,${frameData}`} 
            alt="Video Stream" 
            className="w-full h-auto"
            onLoad={() => console.log(`${title} 이미지 로드 완료`)}
            onError={(e) => console.error(`${title} 이미지 로드 실패:`, e)}
          />
        ) : (
          <div className="text-center">
            <p className="text-gray-500 mb-2">
              {isStreaming ? "서버로부터 영상 수신 대기 중..." : "스트리밍이 중지되었습니다."}
            </p>
            <p className="text-xs text-gray-600">
              프레임 데이터: {debugInfo.hasFrameData ? '수신됨' : '수신 안됨'}
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default VideoStream;
