// /frontend/src/components/VideoStream.js

import React from 'react';

const VideoStream = ({ title, frameData, isStreaming, onStreamClick, personDetected }) => {
  // personDetected 값에 따라 클래스를 동적으로 결정
  const borderClass = personDetected ? 'border-4 blink-border' : 'border-2 border-cyan-500';

  return (
    <div className="p-4 bg-gray-800 rounded-lg shadow-lg cursor-pointer" onClick={onStreamClick}>
      <h2 className="text-xl font-semibold mb-2 text-white">{title}</h2>
      <div
        className={`bg-black rounded-md aspect-video flex items-center justify-center transition-all duration-300 ${borderClass}`}
      >
        {frameData ? (
          <img src={`data:image/jpeg;base64,${frameData}`} alt="Video Stream" className="w-full h-auto" />
        ) : (
          <p className="text-gray-500">{isStreaming ? "서버로부터 영상 수신 대기 중..." : "스트리밍이 중지되었습니다."}</p>
        )}
      </div>
    </div>
  );
};

export default VideoStream;
