// /frontend/src/components/FullscreenViewer.js

import React, { useEffect } from 'react';

const FullscreenViewer = ({ title, frameData, onClose }) => {
  useEffect(() => {
    const handleEscKey = (event) => {
      if (event.key === 'Escape') {
        onClose();
      }
    };
    window.addEventListener('keydown', handleEscKey);
    return () => {
      window.removeEventListener('keydown', handleEscKey);
    };
  }, [onClose]);

  if (!frameData) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-80 flex items-center justify-center z-50 p-4">
      <div className="relative w-full h-full max-w-6xl max-h-full flex flex-col">
        <div className="flex justify-between items-center p-2 bg-gray-800 rounded-t-lg">
          <h2 className="text-xl font-semibold text-white">{title}</h2>
          <button
            onClick={onClose}
            className="text-white text-3xl font-bold hover:text-gray-400"
          >
            &times;
          </button>
        </div>
        <div className="flex-grow bg-black flex items-center justify-center rounded-b-lg">
          <img src={`data:image/jpeg;base64,${frameData}`} alt={title} className="max-w-full max-h-full object-contain" />
        </div>
      </div>
    </div>
  );
};

export default FullscreenViewer;
