// /frontend/src/components/VideoStream.js (수정)

import React, { useState, useEffect } from 'react';
import { subscribeToEvent, unsubscribeFromEvent, sendEvent } from '../services/socket';

const VideoStream = ({ title, streamEvent }) => { // <-- props로 title과 streamEvent를 받음
  const [frame, setFrame] = useState('');
  const [isStreaming, setIsStreaming] = useState(false);

  useEffect(() => {
    // streamEvent prop이 변경될 때마다 스트리밍 시작 로직 실행
    if (streamEvent) {
        // 기존 스트림 중지
        if (isStreaming) {
            sendEvent('stop_stream');
        }
        sendEvent(streamEvent.type, streamEvent.payload);
        setIsStreaming(true);
    }
  }, [streamEvent]);

  useEffect(() => {
    subscribeToEvent('video_frame', (data) => {
      setFrame(`data:image/jpeg;base64,${data.image}`);
    });

    return () => {
      unsubscribeFromEvent('video_frame');
      if (isStreaming) {
        sendEvent('stop_stream');
      }
    };
  }, [isStreaming]);

  const handleStop = () => {
    sendEvent('stop_stream');
    setIsStreaming(false);
    setFrame('');
  };

  return (
    <div className="p-4 bg-gray-800 rounded-lg shadow-lg">
      <h2 className="text-xl font-semibold mb-4 text-white">{title}</h2>
      <div className="bg-black border-2 border-cyan-500 rounded-md min-h-[480px] flex items-center justify-center">
        {frame ? (
          <img src={frame} alt="Video Stream" className="w-full h-auto" />
        ) : (
          <p className="text-gray-500">{isStreaming ? "서버로부터 영상 수신 대기 중..." : "스트리밍이 중지되었습니다."}</p>
        )}
      </div>
      <div className="mt-4 flex justify-center space-x-4">
        {/* 시작 버튼은 이제 부모 컴포넌트에서 관리 */}
        <button 
          onClick={handleStop}
          disabled={!isStreaming}
          className="px-6 py-2 font-bold text-white bg-red-600 rounded-lg hover:bg-red-700 disabled:bg-gray-500 disabled:cursor-not-allowed">
          스트리밍 중지
        </button>
      </div>
    </div>
  );
};

export default VideoStream;
