// /frontend/src/components/Dashboard.js (최종 수정본)

import React, { useEffect, useState, useCallback, useRef } from 'react';
import VideoStream from './VideoStream';
import EventLog from './EventLog';
import TestModePanel from './TestModePanel';
import FullscreenViewer from './FullscreenViewer';
import EventDetailViewer from './EventDetailViewer';
import { initSocket, disconnectSocket, subscribeToEvent, sendEvent } from '../services/socket';
import alertSound from '../assets/alarm.mp3';

const Dashboard = () => {
  // 서버로부터 받은 메시지를 저장할 상태
  const [serverMessage, setServerMessage] = useState('');
  // 현재 모드 상태 (실시간 감시 모드 또는 시험 영상 분석 모드)
  const [mode, setMode] = useState('live');
  // 로컬 PC 웹캠 ID 목록
  // const cameraIds = [0, 1];
  const cameraIds = [0];
  // 각 카메라의 프레임과 스트리밍 상태를 객체로 관리
  // const [liveFrames, setLiveFrames] = useState({ 0: { rgb: null, tir: null }, 1: { rgb: null, tir: null } });
  // const [isStreaming, setIsStreaming] = useState({ 0: false, 1: false });
  const [liveFrames, setLiveFrames] = useState({ 0: { rgb: null, tir: null } });
  const [isStreaming, setIsStreaming] = useState({ 0: false });
  // 각 카메라별 사람 탐지 여부 상태
  // const [personDetected, setPersonDetected] = useState({ 0: false, 1: false });
  const [personDetected, setPersonDetected] = useState({ 0: false });
  // 전체 화면으로 표시할 스트림의 상태
  const [fullscreenStream, setFullscreenStream] = useState(null);
  // 전체 화면 이벤트 뷰어 상태
  const [fullViewEvent, setFullViewEvent] = useState(null);
  // 오디오 재생을 위한 ref
  const audioRef = useRef(null);

  // VideoStream 컴포넌트를 클릭했을 때 호출될 함수
  const handleStreamClick = (title, frameData) => {
    setFullscreenStream({ title, frameData });
  };
  // 전체 화면 뷰어를 닫는 함수
  const handleCloseFullscreen = () => {
    setFullscreenStream(null);
  };

  // 전체 화면 이벤트를 열고 닫는 함수
  const handleOpenFullEvent = (event) => {
    setFullViewEvent(event);
  };
  // 전체 화면 이벤트를 닫는 함수
  const handleCloseFullEvent = () => {
    setFullViewEvent(null);
  };

  // 소켓 연결/해제 전용 useEffect (컴포넌트 로드/언로드 시 딱 한 번만 실행)
  useEffect(() => {
    initSocket();
    
    subscribeToEvent('response', (data) => {
      setServerMessage(data.message);
    });
    
    subscribeToEvent('video_frame', (data) => {
      if (typeof data.camera_id === 'number') {
        setLiveFrames(prev => ({
          ...prev,
          [data.camera_id]: { rgb: data.rgb, tir: data.tir }
        }));
        // 백엔드로부터 받은 person_detected 플래그로 상태 업데이트
        setPersonDetected(prev => ({ ...prev, [data.camera_id]: data.person_detected }));
      }
    });

    // 컴포넌트가 사라질 때 소켓 연결을 완전히 해제
    return () => {
      disconnectSocket();
    };
  }, []); // <-- 의존성 배열을 비워서 최초 1회만 실행되도록 함

  // 실시간 스트림 시작 및 중지 함수
  const startAllStreams = useCallback(() => {
    cameraIds.forEach(id => {
      sendEvent('start_stream', { camera_id: id });
      setIsStreaming(prev => ({ ...prev, [id]: true }));
    });
  }, [cameraIds]);

  // 모든 스트림을 중지하는 함수
  const stopAllStreams = useCallback(() => {
    cameraIds.forEach(id => {
      sendEvent('stop_stream', { camera_id: id });
    });
    setIsStreaming({ 0: false, 1: false });
    setLiveFrames({ 0: { rgb: null, tir: null }, 1: { rgb: null, tir: null } });
    setPersonDetected({ 0: false, 1: false });
  }, [cameraIds]);

  // 모드 변경에 따라 스트림 시작/중지
  useEffect(() => {
    if (mode === 'live') {
      startAllStreams();
    }
    return () => {
      stopAllStreams();
    };
  }, [mode, startAllStreams, stopAllStreams]);

  // 오디오 재생을 위한 useEffect
  useEffect(() => {
    if (personDetected[0] || personDetected[1]) {
      if (audioRef.current) {
        audioRef.current.play().catch(error => console.error("오디오 재생 오류:", error));
      }
    }
  }, [personDetected]);

  // 모드 변경 핸들러
  const handleModeChange = (newMode) => {
    setMode(newMode);
  };

  return (
    <div>
      <div className="flex justify-center space-x-4 mb-4">
        <button onClick={() => handleModeChange('live')} className={`px-6 py-2 font-bold text-white rounded-lg transition-colors ${mode === 'live' ? 'bg-cyan-600' : 'bg-gray-600 hover:bg-gray-700'}`}>
          실시간 다중 감시
        </button>
        <button onClick={() => handleModeChange('test')} className={`px-6 py-2 font-bold text-white rounded-lg transition-colors ${mode === 'test' ? 'bg-cyan-600' : 'bg-gray-600 hover:bg-gray-700'}`}>
          시험 영상 분석
        </button>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 lg:items-start">
        <div className="lg:col-span-2 space-y-6">
          {mode === 'live' ? (
            <div className="grid grid-cols-2 gap-4">
              <VideoStream title="카메라 1 - RGB" frameData={liveFrames[0]?.rgb} isStreaming={isStreaming[0]} onStreamClick={() => handleStreamClick('카메라 1 - RGB', liveFrames[0]?.rgb)} personDetected={personDetected[0]} />
              <VideoStream title="카메라 1 - TIR" frameData={liveFrames[0]?.tir} isStreaming={isStreaming[0]} onStreamClick={() => handleStreamClick('카메라 1 - TIR', liveFrames[0]?.tir)} personDetected={personDetected[0]} />
              {/* <VideoStream title="카메라 2 - RGB" frameData={liveFrames[1]?.rgb} isStreaming={isStreaming[1]} onStreamClick={() => handleStreamClick('카메라 2 - RGB', liveFrames[1]?.rgb)} personDetected={personDetected[1]} /> */}
              {/* <VideoStream title="카메라 2 - TIR" frameData={liveFrames[1]?.tir} isStreaming={isStreaming[1]} onStreamClick={() => handleStreamClick('카메라 2 - TIR', liveFrames[1]?.tir)} personDetected={personDetected[1]} /> */}
            </div>
          ) : (
            <TestModePanel />
          )}
        </div>
        
        <div className="space-y-6 h-full">
          {serverMessage && <div className="p-4 bg-blue-900 rounded-lg text-center mb-4">{serverMessage}</div>}
          <EventLog onOpenFull={handleOpenFullEvent} />
        </div>
      </div>

      {/* fullscreenStream 상태가 유효할 때만 FullscreenViewer를 렌더링*/}
      {fullscreenStream && (
        <FullscreenViewer
          title={fullscreenStream.title}
          frameData={fullscreenStream.frameData}
          onClose={handleCloseFullscreen}
        />
      )}

      {/* 이벤트 상세 뷰어 렌더링 */}
      {fullViewEvent && (
        <EventDetailViewer event={fullViewEvent} onClose={handleCloseFullEvent} />
      )}

      {/* 오디오 태그에 ref와 src 연결 */}
      <audio ref={audioRef} src={alertSound} />
    </div>
  );
};

export default Dashboard;
