// /frontend/src/components/Dashboard.js (ÏµúÏ¢Ö ÏàòÏ†ïÎ≥∏)

import React, { useEffect, useState, useCallback } from 'react';
import VideoStream from './VideoStream';
import EventLog from './EventLog';
import TestModePanel from './TestModePanel';
import { initSocket, disconnectSocket, subscribeToEvent, sendEvent } from '../services/socket';

const Dashboard = () => {
  const [serverMessage, setServerMessage] = useState('');
  const [mode, setMode] = useState('live');

  // --- üëá ÏàòÏ†ïÎêú Î∂ÄÎ∂Ñ ÏãúÏûë üëá ---
  // Î°úÏª¨ PC ÏõπÏ∫† ID Î™©Î°ù (0Î≤à, 1Î≤à Ïπ¥Î©îÎùº)
  const cameraIds = [0, 1];

  // Í∞Å Ïπ¥Î©îÎùºÏùò RGB/TIR ÌîÑÎ†àÏûÑÍ≥º Ïä§Ìä∏Î¶¨Î∞ç ÏÉÅÌÉúÎ•º Í∞ùÏ≤¥Î°ú Í¥ÄÎ¶¨
  const [liveFrames, setLiveFrames] = useState({ 0: { rgb: null, tir: null }, 1: { rgb: null, tir: null } });
  const [isStreaming, setIsStreaming] = useState({ 0: false, 1: false });

  const startAllStreams = useCallback(() => {
    cameraIds.forEach(id => {
      sendEvent('start_stream', { camera_id: id });
      setIsStreaming(prev => ({ ...prev, [id]: true }));
    });
  }, [cameraIds]);

  const stopAllStreams = useCallback(() => {
    cameraIds.forEach(id => {
      sendEvent('stop_stream', { camera_id: id });
    });
    setIsStreaming({ 0: false, 1: false });
    setLiveFrames({ 0: { rgb: null, tir: null }, 1: { rgb: null, tir: null } });
  }, [cameraIds]);
  // --- üëÜ ÏàòÏ†ïÎêú Î∂ÄÎ∂Ñ ÎÅù üëÜ ---

  useEffect(() => {
    initSocket();
    subscribeToEvent('response', (data) => {
      setServerMessage(data.message);
    });
    
    // ÏàòÏ†ï: video_frame Ïù¥Î≤§Ìä∏ Ìï∏Îì§Îü¨Í∞Ä rgb, tir Îç∞Ïù¥ÌÑ∞Î•º Íµ¨Î∂ÑÌïòÏó¨ ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
    subscribeToEvent('video_frame', (data) => {
        if (typeof data.camera_id === 'number') { // ÎùºÏù¥Î∏å Ïä§Ìä∏Î¶º (0, 1)Îßå Ï≤òÎ¶¨
            setLiveFrames(prev => ({
                ...prev,
                [data.camera_id]: { rgb: data.rgb, tir: data.tir }
            }));
        }
    });

    if (mode === 'live') {
      startAllStreams();
    }

    return () => {
      if (mode === 'live') {
        stopAllStreams();
      }
      disconnectSocket();
    };
  }, [mode, startAllStreams, stopAllStreams]);

  const handleModeChange = (newMode) => {
    setMode(newMode);
  };

  return (
    <div>
      <div className="flex justify-center space-x-4 mb-4">
        <button onClick={() => handleModeChange('live')} className={`px-6 py-2 font-bold text-white rounded-lg transition-colors ${mode === 'live' ? 'bg-cyan-600' : 'bg-gray-600 hover:bg-gray-700'}`}>
          Ïã§ÏãúÍ∞Ñ Îã§Ï§ë Í∞êÏãú
        </button>
        <button onClick={() => handleModeChange('test')} className={`px-6 py-2 font-bold text-white rounded-lg transition-colors ${mode === 'test' ? 'bg-cyan-600' : 'bg-gray-600 hover:bg-gray-700'}`}>
          ÏãúÌóò ÏòÅÏÉÅ Î∂ÑÏÑù
        </button>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 lg:items-start">
        <div className="lg:col-span-2 space-y-6">
          {mode === 'live' ? (
            // ÏàòÏ†ï: ÏöîÏ≤≠ÌïòÏã† 4Î∂ÑÌï† ÌôîÎ©¥ Î†àÏù¥ÏïÑÏõÉ
            <div className="grid grid-cols-2 gap-4">
              <VideoStream title="Ïπ¥Î©îÎùº 1 - RGB" frameData={liveFrames[0]?.rgb} isStreaming={isStreaming[0]} />
              <VideoStream title="Ïπ¥Î©îÎùº 1 - TIR" frameData={liveFrames[0]?.tir} isStreaming={isStreaming[0]} />
              <VideoStream title="Ïπ¥Î©îÎùº 2 - RGB" frameData={liveFrames[1]?.rgb} isStreaming={isStreaming[1]} />
              <VideoStream title="Ïπ¥Î©îÎùº 2 - TIR" frameData={liveFrames[1]?.tir} isStreaming={isStreaming[1]} />
            </div>
          ) : (
            <TestModePanel />
          )}
        </div>
        
        <div className="space-y-6 h-full">
          {serverMessage && <div className="p-4 bg-blue-900 rounded-lg text-center mb-4">{serverMessage}</div>}
          <EventLog />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
