// src/pages/LoginPage.js
import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthContext from '../context/AuthContext';

const LoginPage = () => {
    // ... (useState, handleSubmit 등 함수 부분은 동일) ...
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const { login } = useContext(AuthContext);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        try {
        const success = await login(username, password);
        if (success) {
            navigate('/');
        } else {
            // 이 경우는 거의 발생하지 않지만, 안전을 위해 남겨둡니다.
            setError('아이디 또는 비밀번호가 올바르지 않습니다.');
        }
    } catch (err) {
        // AuthContext에서 던진 에러를 여기서 잡습니다.
        if (err.response && err.response.status === 401) {
            // 401 Unauthorized 에러는 아이디/비밀번호 불일치입니다.
            setError('아이디 또는 비밀번호가 올바르지 않습니다.');
        } else {
            // 그 외 422, 500 등 다른 모든 에러는 서버 문제로 간주합니다.
            setError('서버와 통신 중 오류가 발생했습니다. 잠시 후 다시 시도해주세요.');
        }
    }
    };

    return (
        <div className="flex items-center justify-center h-screen bg-gray-900">
            <div className="w-full max-w-md p-8 space-y-8 bg-gray-800 rounded-lg shadow-lg">
                <div className="text-center">
                    <h1 className="text-3xl font-bold text-cyan-400">IGS</h1>
                    {/* 이 부분에 className을 추가했습니다. */}
                    <h5 className="mt-1 text-lg font-semibold text-cyan-300">Intelligent Guard System</h5>
                    <p className="mt-2 text-gray-400">경계 감시 시스템 로그인</p>
                </div>
                <form className="space-y-6" onSubmit={handleSubmit}>
                    {/* ... (form 내용은 동일) ... */}
                    {error && <p className="text-red-500 text-center">{error}</p>}
                    <div>
                        <input
                            type="text"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            placeholder="아이디" 
                            className="w-full px-4 py-2 text-white bg-gray-700 border border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-cyan-500"
                            required
                        />
                    </div>
                    <div>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="비밀번호"
                            className="w-full px-4 py-2 text-white bg-gray-700 border border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-cyan-500"
                            required
                        />
                    </div>
                    <button type="submit" className="w-full px-4 py-2 font-bold text-white bg-cyan-600 rounded-lg hover:bg-cyan-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-cyan-500">
                        로그인
                    </button>
                </form>
            </div>
        </div>
    );
};

export default LoginPage;
