// /frontend/src/components/VideoStream.js (수정된 최종본)

import React, { useState, useEffect } from 'react';

const VideoStream = ({ title, frameData, isStreaming, onStreamClick, personDetected }) => {
  // 깜빡임 효과를 위한 상태 추가
  const [isBlinking, setIsBlinking] = useState(false);

  useEffect(() => {
    // personDetected prop이 true로 변경될 때마다 깜빡임 효과를 활성화
    if (personDetected) {
      setIsBlinking(true);
      const timer = setTimeout(() => {
        setIsBlinking(false);
      }, 500); // 0.5초마다 깜빡이는 효과를 위해 상태 변경
      return () => clearTimeout(timer);
    } else {
      setIsBlinking(false);
    }
  }, [personDetected, isBlinking]); // isBlinking을 의존성에 추가하여 반복 실행

  // 사람 탐지 여부에 따라 테두리 스타일 동적 변경
  const borderColorClass = personDetected ? (isBlinking ? 'border-red-500' : 'border-yellow-500') : 'border-cyan-500';
  const borderWidthClass = personDetected ? 'border-4' : 'border-2';

  return (
    <div className="p-4 bg-gray-800 rounded-lg shadow-lg cursor-pointer" onClick={onStreamClick}>
      <h2 className="text-xl font-semibold mb-2 text-white">{title}</h2>
      <div
        className={`bg-black rounded-md aspect-video flex items-center justify-center transition-all duration-300 ${borderWidthClass} ${borderColorClass}`}
      >
        {frameData ? (
          <img src={`data:image/jpeg;base64,${frameData}`} alt="Video Stream" className="w-full h-auto" />
        ) : (
          <p className="text-gray-500">{isStreaming ? "서버로부터 영상 수신 대기 중..." : "스트리밍이 중지되었습니다."}</p>
        )}
      </div>
    </div>
  );
};

export default VideoStream;
