// /frontend/src/components/AdminPanel.js

import React, { useState, useEffect, useCallback } from 'react';

const AdminPanel = () => {
    const [users, setUsers] = useState([]);
    const [cameras, setCameras] = useState([]);
    const [newUsername, setNewUsername] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [newFullName, setNewFullName] = useState('');
    const [newRank, setNewRank] = useState('');
    const [newRole, setNewRole] = useState('USER');
    const [message, setMessage] = useState({ type: '', text: '' });

    // 사용자 목록을 가져오는 함수
    const fetchUsers = useCallback(async () => {
        const response = await fetch('/api/users');
        const data = await response.json();
        if (response.ok) setUsers(data);
    }, []);

    // 카메라 목록을 가져오는 함수
    const fetchCameras = useCallback(async () => {
        const response = await fetch('/api/admin/cameras');
        const data = await response.json();
        if (response.ok) setCameras(data);
    }, []);

    // 컴포넌트가 처음 로드될 때 사용자 및 카메라 목록을 불러옵니다.
    useEffect(() => {
        fetchUsers();
        fetchCameras();
    }, [fetchUsers, fetchCameras]);

    // 신규 사용자 추가 폼 제출 처리 함수
    const handleAddUser = async (e) => {
        e.preventDefault();
        setMessage({ type: '', text: '' });
        const newUser = { username: newUsername, password: newPassword, full_name: newFullName, rank: newRank, role: newRole };
        
        try {
            const response = await fetch('/api/users', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(newUser),
            });
            const data = await response.json();
            if (response.ok) {
                setMessage({ type: 'success', text: data.message });
                fetchUsers(); // 목록 새로고침
                setNewUsername(''); setNewPassword(''); setNewFullName(''); setNewRank(''); setNewRole('USER');
            } else {
                setMessage({ type: 'error', text: data.error });
            }
        } catch (err) {
            setMessage({ type: 'error', text: '서버 통신 오류' });
        }
    };

    // 사용자 삭제 처리 함수
    const handleDeleteUser = async (user) => {
        if (window.confirm(`정말로 '${user.username}' 사용자를 삭제하시겠습니까?`)) {
            try {
                const response = await fetch(`/api/users/${user.id}`, { method: 'DELETE' });
                const data = await response.json();
                if (response.ok) {
                    setMessage({ type: 'success', text: data.message });
                    fetchUsers(); // 목록 새로고침
                } else {
                    setMessage({ type: 'error', text: data.error });
                }
            } catch (err) {
                setMessage({ type: 'error', text: '서버 통신 오류' });
            }
        }
    };

    return (
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* 사용자 관리 패널 */}
            <div className="card p-4">
                <h2 className="text-2xl font-semibold mb-4 border-b border-gray-600 pb-2">사용자 관리</h2>
                
                {/* 신규 사용자 추가 폼 */}
                <form onSubmit={handleAddUser} className="mb-6 bg-gray-800 p-4 rounded-lg">
                    <h3 className="text-lg font-semibold mb-2">신규 사용자 추가</h3>
                    <div className="grid grid-cols-2 gap-4">
                        <input value={newUsername} onChange={e => setNewUsername(e.target.value)} className="form-input" placeholder="사용자명 (ID)" required />
                        <input value={newPassword} onChange={e => setNewPassword(e.target.value)} className="form-input" type="password" placeholder="비밀번호" required />
                        <input value={newFullName} onChange={e => setNewFullName(e.target.value)} className="form-input" placeholder="이름" required />
                        <input value={newRank} onChange={e => setNewRank(e.target.value)} className="form-input" placeholder="계급/직책" />
                        <select value={newRole} onChange={e => setNewRole(e.target.value)} className="form-input">
                            <option value="USER">USER</option>
                            <option value="ADMIN">ADMIN</option>
                        </select>
                        <button type="submit" className="login-button col-span-2">추가</button>
                    </div>
                    {message.text && <p className={`mt-2 text-sm ${message.type === 'error' ? 'text-red-500' : 'text-green-500'}`}>{message.text}</p>}
                </form>

                <div className="overflow-x-auto">
                    <table className="min-w-full text-left">
                        <thead>
                            <tr className="border-b border-gray-600">
                                <th className="p-2">사용자명</th>
                                <th className="p-2">이름</th>
                                <th className="p-2">계급/직책</th>
                                <th className="p-2">역할</th>
                                <th className="p-2">작업</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.map(user => (
                                <tr key={user.id} className="border-b border-gray-700 hover:bg-gray-800">
                                    <td className="p-2">{user.username}</td>
                                    <td className="p-2">{user.full_name}</td>
                                    <td className="p-2">{user.rank}</td>
                                    <td className="p-2">{user.role}</td>
                                    <td className="p-2">
                                        <button onClick={() => handleDeleteUser(user)}
                                            className="text-red-500 hover:text-red-400 text-sm"
                                            disabled={user.username === 'admin'}>
                                            삭제
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>

            {/* 카메라 관리 패널 */}
            <div className="card p-4">
                <h2 className="text-2xl font-semibold mb-4 border-b border-gray-600 pb-2">카메라 관리</h2>
                <div className="overflow-x-auto">
                    <table className="min-w-full text-left">
                        <thead className="border-b border-gray-600">
                            <tr>
                                <th className="p-2">ID</th>
                                <th className="p-2">이름</th>
                                <th className="p-2">위치</th>
                                <th className="p-2">상태</th>
                            </tr>
                        </thead>
                        <tbody>
                            {cameras.map(cam => (
                                <tr key={cam.id} className="border-b border-gray-700 hover:bg-gray-800">
                                    <td className="p-2">{cam.id}</td>
                                    <td className="p-2">{cam.camera_name}</td>
                                    <td className="p-2">{cam.location}</td>
                                    <td className="p-2">{cam.status}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

export default AdminPanel;
