// /frontend/src/components/VideoStream.js

import React from 'react';

const VideoStream = ({ title, frameData, isStreaming, onStreamClick, personDetected }) => {
  // personDetected 값에 따라 부모 div의 배경 클래스를 동적으로 결정
  const containerClass = personDetected
    ? 'bg-blink' // 탐지 시: 깜빡임 애니메이션 배경
    : 'bg-gray-800'; // 평상시: 일반 배경

  const borderClass = personDetected ? 'border-red-500' : 'border-cyan-500';

  return (
    // 수정: 동적 배경 클래스 적용
    <div className={`p-4 rounded-lg shadow-lg cursor-pointer ${containerClass}`} onClick={onStreamClick}>
      <h2 className="text-xl font-semibold mb-2 text-white">{title}</h2>
      <div
        className={`bg-black rounded-md aspect-video flex items-center justify-center transition-colors border-2 ${borderClass}`}
      >
        {frameData ? (
          <img src={`data:image/jpeg;base64,${frameData}`} alt="Video Stream" className="w-full h-auto" />
        ) : (
          <p className="text-gray-500">{isStreaming ? "서버로부터 영상 수신 대기 중..." : "스트리밍이 중지되었습니다."}</p>
        )}
      </div>
    </div>
  );
};

export default VideoStream;
