import React, { useEffect, useState } from 'react';
import VideoStream from './VideoStream';
import EventLog from './EventLog';
import ModelSelector from './ModelSelector';
import { initSocket, disconnectSocket, subscribeToEvent } from '../services/socket';

const Dashboard = () => {
  const [socket, setSocket] = useState(null);
  const [serverMessage, setServerMessage] = useState('');

  useEffect(() => {
    // 컴포넌트 마운트 시 소켓 초기화 및 연결
    const newSocket = initSocket();
    setSocket(newSocket);

    // 서버로부터 오는 일반 응답 메시지를 구독
    subscribeToEvent('response', (data) => {
        setServerMessage(data.message);
    });
    
    // 컴포넌트 언마운트 시 소켓 연결 해제
    return () => {
      disconnectSocket();
    };
  }, []);

  return (
    <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
      <div className="lg:col-span-2 space-y-6">
        {/* 비디오 스트림 컴포넌트 */}
        <VideoStream />
      </div>
      <div className="space-y-6">
        {/* 서버 메시지 출력 */}
        {serverMessage && <div className="p-4 bg-blue-900 rounded-lg text-center">{serverMessage}</div>}
        {/* 모델 선택 컴포넌트 */}
        <ModelSelector />
        {/* 이벤트 로그 컴포넌트 */}
        <EventLog />
      </div>
    </div>
  );
};

export default Dashboard;
